import debug from "debug";
import { bytesToHex as hex } from "@noble/hashes/utils";
export const formatKey = (key) => (key ? hex(key) : "null");
export const formatNode = (node) => node ? `{ ${node.level}:${formatKey(node.key)} | ${hex(node.hash)} }` : "null";
export const formatBound = (bound) => bound ? (bound.inclusive ? `[${formatKey(bound.key)}]` : `(${formatKey(bound.key)})`) : "...";
debug.formatters.h = hex;
debug.formatters.k = formatKey;
debug.formatters.n = formatNode;
debug.formatters.b = formatBound;
export { debug };
